import React, { Component } from 'react'
import axios from 'axios';
const API_URL = 'http://localhost:4000';

class Welcome extends Component {
    state = {
        data : [{}]
    }
    
    
    componentDidMount(){
        axios.get(`http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v0002/?key=A9AF4EC788FFD1B9A079A36B0A93BDFC&steamids=` +this.props.userID)
        .then((response) =>
            this.setState({
                data: response.data.response.players[0]
                
            }),
          )
        .catch(error  => {
            console.log(error)
        })
    }

    // newUser(e){
    //     e.preventDefault()
    //     axios.post(`${API_URL}/userinfo`, "steamid": request.body.steamid,
    //     "communityvisibilitystate": request.body.communityvisibilitystate,
    //     "profilestate": request.body.profilestate,
    //     "personaname": request.body.personaname,
    //     "commentpermission": request.body.commentpermission,
    //     "profileurl": request.body.profileurl,
    //     "avatar": request.body.avatar,
    //     "avatarmedium": request.body.avatarmedium,
    //     "avatarfull": request.body.avatarfull,
    //     "avatarhash": request.body.avatarhash,
    //     "lastlogoff": request.body.lastlogoff,
    //     "personastate": request.body.personastate,
    //     "primaryclanid": request.body.primaryclanid,
    //     "timecreated": request.body.timecreated,
    //     "personastateflags": request.body.personastateflags,
    //     "loccountrycode": request.body.loccountrycode,
    //     "locstatecode": request.body.locstatecode)
    // }

    render() {
        console.log("this is real data", this.state.data);
        return (
            <div className="welcome">
                {this.props.userID.length === 0 ?
                    <>
                    <h2>Get Started</h2> 
                    <p><a href ={`${API_URL}/auth/openid`}><img src="https://steamcdn-a.akamaihd.net/steamcommunity/public/images/steamworks_docs/english/sits_large_noborder.png"/></a></p>
                    </>
                    :
                    <>
                    <h2>Hello</h2>
                    <div className="welcome__user">
                        <p><img src={this.state.data.avatar} alt="player avatar"/></p>
                        <p>{this.state.data.personaname}</p>
                    </div>
                    <p><a href ={`${API_URL}/auth/logout`}>Logout</a></p>
                    <form id="matchUser" onSubmit={this.newUser}>
                        <button type='submit'>Match me</button>
                    </form>
                    </>
                }  
            </div>
        )
    }
}

export default Welcome
