import React, { Component } from 'react'
import './featured.scss'
import '../../../node_modules/aos/dist/aos.css';
import AOS from 'aos';
import axios from 'axios';
const API_URL = 'http://localhost:4000';
AOS.init({
    duration: 1200
   });

class Featured extends Component {
    constructor(props) { 
        super(props);
     this.state = {
       news1:{},
       news2:{},
       news3:{}
    }
}
    componentDidMount(){
        axios.get(`${API_URL}/getnews`)
        .then((response) =>
        this.setState({
            news1:response.data[0],
            news2:response.data[1],
            news3:response.data[2]
        }),
        )
        .catch(error  => {
            console.log(error)
        })
    }
