import "./header.scss"
import React, { Component } from 'react'
import logo from '../../assets/logo/logo_transparent.png';
import video from '../../assets/video/video.mp4';

class Header extends Component {
    constructor(props){
        super(props)
        this.state =
        {
            number: 0
        }
    }
    onClick = e => {
        e.preventDefault()
        this.setState({
            number: 2
        })
    }
    render() {
        return (
            <div className="header">
                <video className='header__video' autoPlay loop muted>
                    <source src={video} type='video/mp4' />
                </video>
                <div className="header__all">
                    <div className="header__nav">
                        <button className="header__button" onClick={this.onClick}>
                            <nav className="nav">
                                <div className="about"></div>
                                <div className="team"></div>
                                <div className="match"></div>
                            </nav>
                            {this.state.number === 2} ?
                            <div className="realnav">
                                <a href="#">About</a>
                                <a href="#">Team</a>
                                <a href="#">Match</a>
                            </div>
                            :
                            null
                        </button>
                        <img src={logo} alt="steamatch logo" className="nav__logo"/>
                    </div>
                    <h1>Welcome to Steamatch.</h1>
                </div>
            </div>
        )
    }
}

export default Header
