import "./header.scss"
import React, { Component } from 'react'
import logo from '../../assets/logo/logo_transparent.png';
import video from '../../assets/video/video.mp4';

class Header extends Component {
    constructor(props) {
        super(props)
        this.state = {
            width: 0,
            width2: 250
        }
    }
    onClick2 = e => {
        e.preventDefault()
        this.setState({
            width: 250
        })
        }
    onClick3 = e => {
        e.preventDefault()
        this.setState({
            width: 0
        })
    }
    
    render() {
        console.log("this is width", this.state.width)

        return (
            <div className="header">
                <video className='header__video' autoPlay loop muted>
                    <source src={video} type='video/mp4' />
                </video>
                <div className="header__all">
                    <div className="header__nav">
                        <button className="header__button" onClick={this.onClick2}>
                            <nav className="nav">
                                <div className="about"></div>
                                <div className="team"></div>
                                <div className="match"></div>
                            </nav>
                            <div className="realnav" style={{width:this.state.width}}>
                                <button onClick={this.onClick3}>X</button>
                                <a href="#">About</a>
                                <a href="#">Team</a>
                                <a href="#">Match</a>
                            </div>
                        </button>
                        <img src={logo} alt="steamatch logo" className="nav__logo"/>
                    </div>
                    <h1>Welcome to Steamatch.</h1>
                </div>
            </div>
        )
    }
}

export default Header
