import React, { Component } from 'react';
import './main.scss';
import axios from 'axios';



const API_URL = 'http://localhost:4000';

class Main extends Component {
    state = {
        data : {}
    }

    componentDidMount(){
        
    }

    componentDidUpdate(){
        axios.get(`http://api.steampowered.com/ISteamUser/GetPlayerSummaries/v0002/?key=A9AF4EC788FFD1B9A079A36B0A93BDFC&steamids=${this.props.location.search.slice(9)}`)
        .then((response) =>
            this.setState({
                data: response.response.players
                
            }),
            
          )
        .catch(error  => {
            console.log(error)
        })
    }
    render() {
        const userID = this.props.location.search.slice(9);
        console.log(userID);
        console.log(this.state)
        return (
            <div className="main">
                <h1>Welcome to Steamatch.</h1>
                <h2>Log in</h2> 
                <p><a href ={`${API_URL}/auth/openid`}><img src="https://steamcdn-a.akamaihd.net/steamcommunity/public/images/steamworks_docs/english/sits_large_noborder.png"/></a></p>
                <p><a href ='http://localhost:4000/logout'>Logout</a></p>
            </div>
        )
    }
}

export default Main
