import React, { Component } from 'react';
import './main.scss';
import axios from 'axios';
import Video from './Video/Video';


const API_URL = 'http://localhost:4000';


class Main extends Component {
    state = {
        data : {}
    }
    
    componentDidMount(){
        axios.get(`${API_URL}/getplayersummary`)
        .then((response) =>
            this.setState({
                data: response.data
                
            }),
            console.log("this  is rea;", this.data)
          )
        .catch(error  => {
            console.log(error)
        })
    }

    componentDidUpdate(){

    }
    render() {
        const userID = this.props.location.search.slice(9);
        console.log(userID);
        
        console.log("this is realay", this.state.data)
        
        return (
            <div className="main">
                {/* <Video/> */}
                <div className="main__headline">
                    <h1>Welcome to Steamatch.</h1>
                    {/* <img src={this.state.data.response.players.avatarmedium}/> */}
                </div>
                <div className="main__content">
                    {userID.length === 0 ?
                    <>
                    <h2>Get Started</h2> 
                    <p><a href ={`${API_URL}/auth/openid`}><img src="https://steamcdn-a.akamaihd.net/steamcommunity/public/images/steamworks_docs/english/sits_large_noborder.png"/></a></p>
                    <p><a href ={`${API_URL}/auth/logout`}>Logout</a></p>
                    </>
                    :
                    <h2>hello</h2>
                    }   
                </div>
            </div>
        )
    }
}

export default Main
