import React, { Component } from 'react'
import './featured.scss'
import '../../../node_modules/aos/dist/aos.css';
import AOS from 'aos';
import axios from 'axios';
const API_URL = 'http://localhost:4000';
AOS.init({
    duration: 1200
   });

class Featured extends Component {
    constructor(props) { 
        super(props);
     this.state = {
       news1:{},
       news2:{},
       news3:{}
    }
}
    componentDidMount(){
        axios.get(`${API_URL}/getnews`)
        .then((response) =>
        this.setState({
            news1:response.data.appnews.newsitems[0],
            news2:response.data.appnews.newsitems[1],
            news3:response.data.appnews.newsitems[2]
        }),
        )
        .catch(error  => {
            console.log(error)
        })
    }
    render() {
        console.log(this.state);
        return (
            <div className="news" id="news">
                <div className="news__article">
                    <h2>Featured</h2>
                    <div className="new__article--1">
                        <a href={this.state.news1.url} rel="noopener noreferrer">
                            <h3>{this.state.news1.title}</h3>
                        </a>
                    </div>
                </div>
            </div>
        )
    }
}

export default Featured
